      --       ELSE
      IF (old.ARRIVALTYPE = 'OUTBOUND') THEN
        IF EXISTS (SELECT
              1
            FROM DOC_ARRIVAL_HEADER dah
              INNER JOIN DOC_ARRIVAL_DETAILS dad
                ON (dah.organizationId = dad.organizationId
                AND dah.warehouseId = dad.warehouseId
